.typebot-host-bubble {
  padding-right: 12px;
  max-width: 85%;
}

#checkIcon {
  stroke-dasharray: 40;
  stroke-dashoffset: 40;
  z-index: 99;
}

#checkIcon path:nth-child(1) {  
  animation: showIcon 250ms ease forwards;
  animation-delay: 500ms;
}

#checkIcon path:nth-child(2) {  
  animation: showIcon 250ms ease forwards;
  animation-delay: 400ms;
}

.hora {  
    position: absolute;
    bottom: 0px;
    right: 10px;
    font-size: 12px;
    z-index: 995;
    color: grey;
    animation-name: mostrar;
    animation-duration: 2s;
}

.hora2 {  
    position: absolute;
    bottom: 0px;
    right: 26px;
    font-size: 12px;
    z-index: 995;
    color: grey;
    animation-name: mostrar;
    animation-duration: 2s;
}

@keyframes showIcon {
    to { stroke-dashoffset: 0 };
}

@keyframes mostrar {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/********** check style  **********/

.typebot-chat-view > .flex:first-child {
    padding-top: 30px;
}

.typebot-input {
    position: fixed;
    max-width: 100% !important;
    bottom: 100px;
    align-items: center;
    z-index: 999;
    right: 0;
}

.typebot-input .typebot-button {
    background: #008a7c;
    border-radius: 50%;
    color: #fff;
    position: relative;
    width: 50px;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.typebot-container {
    background-size: initial!important;
    background-repeat: repeat-x!important;
}

.typebot-host-bubble>.bubble-typing {
  border-radius: 12px 12px 12px 0;
}

.typebot-host-bubble>.bubble-typing:after {
    position: absolute;
    content: "";
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0px 10px 10px 0;
    border-color: transparent #fff transparent transparent;
    bottom: 0;
    left: -9px;
    -moz-transform: scaleY(-1);
    -o-transform: scaleY(-1);
    -webkit-transform: scaleY(-1);
    transform: scaleY(-1);
}

.typebot-guest-bubble {
    position: relative;
    border-radius: 12px 12px 0 12px;
    padding-bottom: 19px;
    min-width: 70px;
}

.typebot-guest-bubble:after {
    position: absolute;
    content: "";
    width: 0;
    height: 0;
    border-style: solid;
    -moz-transform: scaleY(-1);
    -o-transform: scaleY(-1);
    -webkit-transform: scaleY(-1);
    transform: scaleY(-1);
}

.typebot-guest-bubble:after {
    border-width: 0px 0 10px 10px;
    border-color: transparent transparent transparent #e1ffc7;
    bottom: 0;
    right: -9px;
    border-style: solid;
}

