// Desativar DevTools
window.addEventListener('keydown', function(event) {
    if (event.key === 'F12' || (event.ctrlKey && event.shiftKey && event.key === 'C') || (event.ctrlKey && event.key === 'u')) {
        event.preventDefault();
        event.stopPropagation();
    }
});

// Desativar o menu de contexto (bot√£o direito do mouse)
window.addEventListener('contextmenu', function(event) {
    event.preventDefault();
});

theme.customCss:
/********** check style  **********/
.typebot-host-bubble {
  padding-right: 12px;
}

#checkIcon {
  stroke-dasharray: 40;
  stroke-dashoffset: 40;
  z-index: 99;
}

#checkIcon path:nth-child(1) {  
  animation: showIcon 250ms ease forwards;
  animation-delay: 500ms;
}


#checkIcon path:nth-child(2) {  
  animation: showIcon 250ms ease forwards;
  animation-delay: 400ms;
}


@keyframes showIcon {
  to { stroke-dashoffset: 0 };
}
/********** check style  **********/

.typebot-chat-view > .flex:first-child {
    padding-top: 30px;
}

.typebot-input {
    position: fixed;
    bottom: 0;
    align-items: center;
    z-index: 999;
    right: 0;
}

.typebot-input .typebot-button {
    background: #008a7c;
    border-radius: 50%;
    color: #fff;
    position: relative;
    width: 50px;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.typebot-container {
    background-size: initial!important;
    background-repeat: repeat-x!important;
}

.typebot-host-bubble>.bubble-typing {
  border-radius: 0 5px 5px 5px;
}

.typebot-host-bubble>.bubble-typing:after {
    position: absolute;
    content: "";
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0px 10px 10px 0;
    border-color: transparent #fff transparent transparent;
    top: 0;
    left: -10px;
}

.typebot-guest-bubble {
  position: relative;
  border-radius: 5px 0 5px 5px;
}

.typebot-guest-bubble:after {
  position: absolute;
  content: "";
  width: 0;
  height: 0;
  border-style: solid;
}

.typebot-guest-bubble:after {
    border-width: 0px 0 10px 10px;
    border-color: transparent transparent transparent #e1ffc7;
    top: 0;
    right: -10px;
    border-style: solid;
}

iframe[src=""] {
  position: fixed;
  top: 0;
  left: 0;
  display: relative;
  width: 100%;
  height: 100% !important;
  z-index: 999999;
  border: none;
  padding: 0;
  margin: 0;
}


.info-container {
font-size: 14px;
}

@media (max-width: 500px) {
.info-container {
max-width: 400px !important;
}
}

.typebot-host-bubble > .bubble-typing { color: white }
a#lite-badge {
  display: none!important;
}
